import torch.utils.data as datafrom PIL import Imageclass CombineDataset(data.Dataset):    def __init__(self, images1, images2, labels1, labels2, transforms=None):        self.images = images1 + images2        self.labels = labels1 + labels2        self.transforms = transforms    def __len__(self):        return len(self.labels)    def __getitem__(self, index):        """        Args:            index (int): Index        Returns:            tuple: (image, target) where target is index of the target class.        """        img = Image.open(self.images[index]).convert('RGB')        target = self.labels[index]        if self.transforms:            img = self.transforms(img)        return img, target